pragma solidity 0.8.9;
import "@openzeppelin/contracts/access/Ownable.sol";

contract Allowance is Ownable {
    
    event AllowanceChange(address indexed _forUser, address indexed _ChangedBy, uint _newAllowance, uint _oldAllowance ,int _difference);
    mapping(address => uint) public allowance;
    int _x;

    function TrueOwner() internal view returns(bool) {
        return owner() == msg.sender;
    }
    
    modifier Permissions(uint _amount) {
        require(TrueOwner() || allowance[msg.sender] >= _amount, "The amount exceeds your allowance.");
        _;
    }

    function SetAllowance(address _user, uint _amount) public onlyOwner {
       emit AllowanceChange(_user, msg.sender, _amount, allowance[_user], int(_amount) - int(allowance[_user]));
        allowance[_user] = _amount;
    }
    
     function UpdateAllowance(address _user, uint _amount) internal Permissions(_amount) {
        emit AllowanceChange(_user, msg.sender, allowance[_user] - _amount, allowance[_user], _x - int(_amount));
        allowance[_user] -= _amount;
    }
    
}
